function slides() {
    $(this).hasClass("current") ? (side.animate({
        left: 0
    }, 500, function() {
        btn.attr({
            src: "../Assets/Pc/images/arrow.png"
        }), 
        btn.removeClass("current")
    }), 
    inside_content.animate({
        left: "15%"
    }, 500, function() {
        var t = $(document).width() - $(".inside_side").width();
        inside_content.width(t)
    })) : (side.animate({
        left: "-15%"
    }, 500, function() {
        btn.attr({
            src: "../Assets/Pc/images/arrow_click.png"
        }), btn.addClass("current")
    }), 
    inside_content.animate({
        left: 0
    }, 500, function() {
        $(this).width($(document).width())
    }))
}
var btn = $(".right_nav").children(".shrink"),
    side = $(".inside_side"),
    inside_content = $(".inside_content"),
    w = $(document).width() - side.width();
console.log(w), inside_content.width(w), btn.on("click", slides);